#include <bits/stdc++.h>
using namespace std;
int editDistance(string s, string t){
  if(s == "" || t == ""){
    return max(s.size(),t.size());
  }
  if(s[0] == t[0]){
    return editDistance(s.substr(1), t.substr(1));
  }
  else{
    return 1 + min((editDistance(s.substr(1), t)), min(editDistance(s,t.substr(1)), editDistance(s.substr(1),t.substr(1))));
  }
}

int main(){
  string s = "";
  string t = "a";
  cout<<editDistance(s,t);
  return 0;
}
